import java.util.*;
public class A3Q3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int n;
		System.out.println("Enter a number");
		n=sc.nextInt();
		if(n%2==0)
		System.out.println("Even number");
		else
		System.out.println("Odd number");
	}

}
