
public class A6Q15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		area(10,20);
		area(5);
		area(5.5);
		area(10.1,20.2);
	}
	public static void area (int a , int b)
	{
		System.out.println("The area of the triangle is ="+(0.5*a*b));

	}
	public static void area(int c)
	{
		System.out.println("The area of square is ="+(c*c)); 
	}
	public static void area(double d)
	{
		System.out.println("The area of the circle is ="+(Math.PI*d*d)); 
	}
	public static void area (double e , double f)
	{
		System.out.println("The area of the rectangle is ="+(e*f)); 
	}
	}


